import xlsxwriter
from itertools import chain, combinations
import names
import random
class SheetWriter(object):

	def __init__(self, fileName, site, password, pid, address, address2, city, state, zipCode, country, cardType, amount):
		self.workbook = xlsxwriter.Workbook('createdSheets/'+str(fileName)+'.xlsx')
		self.worksheet = self.workbook.add_worksheet()
		self.format1 = self.workbook.add_format({'bold': True})

		self.fileName = fileName

		self.format1.set_bg_color('black')
		self.format1.set_font_color('white')
		self.format1.set_align('center')
		self.format1.set_align('vcenter')
		self.site = site
		if site == '1':
			self.worksheet.set_column('A:A', 20)
			self.worksheet.set_column('B:B', 30)
			self.worksheet.set_column('C:C', 10)
			self.worksheet.set_column('E:F', 20)
			self.worksheet.set_column('G:G', 30)
