	def powerset(self, iterable):
		"powerset([1,2,3]) --> () (1,) (2,) (3,) (1,2) (1,3) (2,3) (1,2,3)"
		s = list(iterable)
		combo_list = []
		combos = chain.from_iterable(combinations(s, r) for r in range(len(s)+1))
		if self.country == 'GB':
			for combo in combos:
				if len(combo) == 2:
					
					AddyJig = combo[0]+combo[1]
					combo_list.append(AddyJig)
				else:
					pass
			return combo_list

		else:
			for combo in combos:
				if len(combo) == 4:
					
					AddyJig = combo[0]+combo[1]+combo[2]+combo[3]
					combo_list.append(AddyJig)
				else:
					pass
			return combo_list


	def phn(self):
		n = '0000000000'
		while '9' in n[3:6] or n[3:6]=='000' or n[6]==n[7]==n[8]==n[9]:
			n = str(random.randint(10**9, 10**10-1))
		return '815' + n[3:6] + n[6:]

